def retrieve_value(v, t):
    if v in ['', 'None', None]:
        vv = None
    elif v in ['sample', 'fit']:
        vv = v
    elif t == 'bool':
        if v in ['False', False]:
            vv = False
        elif v in ['True', True]:
            vv = True
    elif t == List[Tuple[float, float]]:
        v = v.replace('{', ' ')
        v = v.replace('}', ' ')
        vv = [tuple([float(x) for x in t.split()]) for t in v.split('   ')]
    elif t == Tuple[float, float]:
        v = v.replace('{', '')
        v = v.replace('}', '')
        v = v.replace('[', '')
        v = v.replace(']', '')
        v = v.replace('(', '')
        v = v.replace(')', '')
        v = v.replace("'", '')
        v = v.replace(",", ' ')
        vv = tuple([float(x) for x in v.split()])
    elif t == tuple or t == list:
        try:
            vv = literal_eval(v)
        except:
            vv = [float(x) for x in v.split()]
            if t == tuple:
                vv = tuple(vv)
    elif type(v) == t:
        vv = v
    # elif type(t) == dict:
    #     vv = v
    else:
        # print(v, t)
        vv = t(v)
    return vv


def get_table_data(values, pars_dict, Nagents):
    data = []
    for i in range(Nagents):
        dic = {}
        for j, (p, t) in enumerate(pars_dict.items()):
            v = values[(i, p)]
            dic[p] = retrieve_value(v, t)
        data.append(dic)
    return data

